package io.scanbot.sdk.reactnative;

import androidx.annotation.NonNull;

import com.facebook.react.ReactPackage;
import com.facebook.react.bridge.NativeModule;
import com.facebook.react.bridge.ReactApplicationContext;
import com.facebook.react.uimanager.ViewManager;

import java.util.ArrayList;
import java.util.Arrays;
import java.util.List;

import io.scanbot.sdk.reactnative.components.barcodecameraview.ScanbotBarcodeCameraViewManager;
import io.scanbot.sdk.reactnative.internal.ScanbotInternal;
import io.scanbot.sdk.reactnative.ui.DefaultUiModule;

public class ScanbotSDKPackage implements ReactPackage {

    @NonNull
    @Override
    public List<NativeModule> createNativeModules(@NonNull ReactApplicationContext reactContext) {
        List<NativeModule> modules = new ArrayList<>();

        modules.add(new ScanbotSDKReactNative(reactContext));
        modules.add(new DefaultUiModule(reactContext));
        modules.add(new ScanbotInternal(reactContext));

        return modules;
    }

    @NonNull
    @Override
    public List<ViewManager> createViewManagers(@NonNull ReactApplicationContext reactContext) {
        return Arrays.asList(new ViewManager[]{
            new ScanbotBarcodeCameraViewManager()
        });
    }

}
