package io.scanbot.sdk.reactnative;

import android.app.Application;

import io.scanbot.sdk.ScanbotSDK;
import io.scanbot.sdk.blob.BlobFactory;
import io.scanbot.sdk.blob.BlobManager;
import io.scanbot.sdk.core.contourdetector.ContourDetector;
import io.scanbot.sdk.persistence.cleanup.Cleaner;
import io.scanbot.sdk.process.ImageProcessor;

class ScanbotSDKWrapper {
    public ScanbotSDK scanbotSDK;
    public Cleaner sdkCleaner;
    public BlobManager blobManager;
    public BlobFactory blobFactory;
    public ImageProcessor imageProcessor;
    public ContourDetector contourDetector;

    ScanbotSDKWrapper(Application application) {
        this.scanbotSDK = new ScanbotSDK(application);
        this.sdkCleaner = scanbotSDK.createCleaner();
        this.blobManager = scanbotSDK.createBlobManager();
        this.blobFactory = scanbotSDK.createBlobFactory();
        this.imageProcessor = scanbotSDK.imageProcessor();
        this.contourDetector = scanbotSDK.createContourDetector();
    }
}
