package io.scanbot.sdk.reactnative.internal;

import com.facebook.react.bridge.ReactApplicationContext;
import com.facebook.react.bridge.ReactContextBaseJavaModule;
import com.facebook.react.bridge.ReactMethod;


public class ScanbotInternal extends ReactContextBaseJavaModule {
    public ScanbotInternal(ReactApplicationContext reactContext) {
        super(reactContext);
    }

    @Override
    public String getName() {
        return "SBSDKInternal";
    }

    @ReactMethod
    public void setNativeValidateProperty(final String result) {
        System.out.println("setNativeValidateProperty NATIVE!!!: " + result);
    }

    @ReactMethod
    public void setNativeProcessProperty(final String result) {
        System.out.println("setNativeProcessProperty NATIVE!!!: " + result);
    }

}